import React from 'react';
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export function ProductList( props:any ): JSX.Element {
  const products = props.products;

  console.log(props,products);
  return (
    <div>
      {products.map((product: any) => {
        <div> 
        <div className="row">
        <div className="col-2"><img className="img-responsive" src="http://placehold.it/100x70" alt="placeholder" />
        </div>
        <div className="col-4">
          <h4 className="product-name"><strong>{ product.item }</strong></h4><h4><small>{product.body}</small></h4>
        </div>
        <div className="col-6">
          <div className="row">
            <div className="col-6 text-end">
              <h6><strong>{ product.price } <span className="text-muted">x</span></strong></h6>
            </div>
            <div className="col-4">
              <input type="text" className="form-control input-sm" value="1" />
            </div>
            <div className="col-2">
              <button type="button" className="btn btn-link btn">
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </div>
          </div>
        </div>
        <hr />
      </div>
      </div>
      })}
    </div>
  )
}
