import React, { useState } from 'react';
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export function ProductList( props:any ): JSX.Element {

  const initialState = [
    {id: 0, name: 'Dog Food', body: "Best dog food for your fur baby!", quantity: 2, price: 15.00},
    {id: 1, name: 'FFXIV', body: "Have you heard of the critically acclaimed MMORPG?", quantity: 1, price: 40.00},
    {id: 2, name: 'Dog Toy', body: "Best dog toys for your fur baby!", quantity: 4, price: 10.00}
  ];

  const [products, setProducts] = useState(initialState);

  const updateQuantity = (index) => (e) => {
    const newArray = products.map((item, i) => {
      if (index === i) {
        return { ...item, quantity: e.target.valueAsNumber };
      } else {
        return item;
      }
    });
    console.log(newArray);
    setProducts(newArray);
  }
  
  function handleUpdateQuantity() {
    products.forEach(item => {
      total += item.price * item.quantity;
    });
  }
  
  function handleDeleteItem(id) {
    const newProduct = products.filter((product) => product.id !== id);
    setProducts(newProduct);
  }

  let total = 0;

  products.forEach(item => {
    total += item.price * item.quantity;
  });

  return (
    <div>
      {products.map((product: any, index) => (
        <div className="row" key={product.id}>
          <div className="col-2"><img className="img-responsive" src="http://placehold.it/100x70" alt="placeholder" />
        </div>
        <div className="col-4">
          <h4 className="product-name"><strong>{ product.name }</strong></h4><h4><small>{product.body}</small></h4>
        </div>
        <div className="col-6">
          <div className="row">
            <div className="col-6 text-end">
              <h6><strong>${ product.price } <span className="text-muted">x</span></strong></h6>
            </div>
            <div className="col-4">
              <input type="number" className="form-control input-sm" defaultValue={ product.quantity } onChange={updateQuantity(index)}/>
            </div>
            <div className="col-2">
              <button type="button" className="btn btn-link btn" onClick={() => handleDeleteItem(product.id)}>
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </div>
          </div>
        </div>
        <hr />
        </div>
      ))}
      <div className="row text-center align-items-center">
                  <div className="col-9">
                    <h6 className="text-end mb-0">Added items?</h6>
                  </div>
                  <div className="col-3">
                    <button type="button" className="btn btn-default btn-sm w-100 border" onClick={handleUpdateQuantity}>
                      Update cart
                    </button>
                  </div>
                </div>
                <div className="card-footer">
                  <div className="row text-center align-items-center">
                    <div className="col-9">
                      <h4 className="text-end mb-0 h5">Total <strong>${ total }</strong></h4>
                    </div>
                    <div className="col-3">
                      <button type="button" className="btn btn-success w-100" >
                        Checkout
                      </button>
                    </div>
                  </div>
                </div>
    </div>
  )
}
