import React, { useContext } from 'react';

const ProductContext = useContext({});

export const ProductProvider = ({ children }) => {
    return (
        <ProductContext.Provider value={{ children }}>
            { children }
        </ProductContext.Provider>
    )
}

export default ProductContext;
