import React, { useState } from 'react';
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export function ProductList( props:any ): JSX.Element {

  const [products, setProducts] = useState([
    {id: 0, name: 'Dog Food', body: "Best dog food for your fur baby!", price: 15.00},
    {id: 1, name: 'FFXIV', body: "Have you heard of the critically acclaimed MMORPG?", price: 40.00},
    {id: 2, name: 'Dog Toy', body: "Best dog toys for your fur baby!", price: 10.00}
  ]);

  function handleDeleteItem(product) {
    alert(product);
  }

  console.log(props,products);
  return (
    <div>
      {products.map((product: any) => (
        <div className="row">
          <div className="col-2"><img className="img-responsive" src="http://placehold.it/100x70" alt="placeholder" />
        </div>
        <div className="col-4">
          <h4 className="product-name" key={product.id}><strong>{ product.name }</strong></h4><h4><small>{product.body}</small></h4>
        </div>
        <div className="col-6">
          <div className="row">
            <div className="col-6 text-end">
              <h6><strong>{ product.price } <span className="text-muted">x</span></strong></h6>
            </div>
            <div className="col-4">
              <input type="text" className="form-control input-sm" value="1" />
            </div>
            <div className="col-2">
              <button type="button" className="btn btn-link btn" onClick={() => handleDeleteItem(product.id)}>
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </div>
          </div>
        </div>
        <hr />
        </div>
      ))}
    </div>
  )
}
